// Clean up text before sending to the AI Agent
const extractedText = items[0].json.text;

// 1. Replace all newline characters (\n and \r) with a single space.
// 2. Replace multiple spaces (or tabs) with a single space.
// 3. Trim leading/trailing whitespace.
const cleanedText = extractedText
    .replace(/(\r\n|\n|\r)/gm, ' ') 
    .replace(/\s\s+/g, ' ')
    .trim();

// Return the cleaned text under a new key for the AI Agent
return [{
    json: {
        paperContent: cleanedText
    }
}];

// This node extracts the clean JSON from the AI Agent's markdown-wrapped output.

const agentOutput = items[0].json.output;

// 1. Clean the output string by removing the surrounding markdown and newline characters.
// The regex finds and removes '```json\n' from the start and '\n```' from the end.
const jsonString = agentOutput.replace(/```json\n|\n```/g, '').trim();

try {
    // 2. Parse the clean string into a JavaScript object.
    const parsedData = JSON.parse(jsonString);

    // 3. Format the data into the final desired structure (Title and Abstract).
    return [{
        json: {
            Title: parsedData.title,
            Abstract: parsedData.abstract
        }
    }];

} catch (error) {
    // 4. Handle any parsing errors (e.g., if the AI did not return valid JSON).
    console.error("Failed to parse JSON from AI Agent output:", error);
    return [{
        json: {
            error: "JSON Parsing Failed: AI output was improperly formatted.",
            rawOutput: agentOutput
        }
    }];
}

// The 'items' array from the previous node (Google Sheets) contains the data.
// We can directly use the 'items' array for filtering and selecting.

// 1. Filter the questions to find all UNANSWERED ones
const unansweredQuestions = items.filter(item => {
    // Check the IsAnswered column. Sticking to user's existing logic (item.json.IsAnswered === false).
    return item.json.IsAnswered === false;
});

// 2. Select the FIRST unanswered question (the next one to be asked)
if (unansweredQuestions.length > 0) {
    // Quiz is ONGOING

    // Get the first item
    const nextQuestionItem = unansweredQuestions[0];

    // Clone the item to safely modify its JSON content
    const outputItem = JSON.parse(JSON.stringify(nextQuestionItem));

    // ADDED: Set the 'Quiz_Completed' key to false
    outputItem.json.Quiz_Completed = false;

    // Return ONLY the next question item with the flag.
    return [outputItem];
} else {
    // Quiz is COMPLETE

    // Create a new item to carry the 'Quiz_Completed: true' flag.
    const outputItem = {
        json: {
            // ADDED: Set the 'Quiz_Completed' key to true
            Quiz_Completed: true
        }
    };

    // NOTE: If your downstream scoring node (Code in JavaScript6) relies on 
    // getting ALL questions in one array when the quiz is complete, you must 
    // also add that nested array here, like:
    // all_questions: items.map(i => i.json) 

    // Return the single item containing the completion flag
    return [outputItem];
}

// Function Node (Calculate Score) - FINAL FIX

// Get the full question list from the nested property 'all_rows'
const allQuestions = items[0].json.all_rows; 

// Defensive check in case of unexpected data flow
if (!Array.isArray(allQuestions)) {
    return [{
        json: {
            total_questions: 0,
            correct_answers: 0,
            score_percentage: 0
        }
    }];
}

// Count total questions and correct answers
const total = allQuestions.length;

// Filter for correct answers, checking for string ('TRUE'/'True') and boolean (true) values
const correct = allQuestions.filter(q => {
    // Check if the answer was marked as correct, covering all common sheet data types
    const isCorrect = q.IsCorrect === true || q.IsCorrect === 'TRUE' || q.IsCorrect === 'True';
    return isCorrect;
}).length;

// Calculate percentage
const percentage = total > 0 ? Math.round((correct / total) * 100) : 0;

// Return the final score data structure
return [{
    json: {
        total_questions: total,
        correct_answers: correct,
        score_percentage: percentage
    }
}];

